{
private:
    CComPtr<IScheduler> scheduler;

public:
    void ConnectToHLK(const wchar_t* controllerName, const wchar_t* password)
    {
        CoInitialize(NULL);
        HRESULT hr = scheduler.CoCreateInstance(__uuidof(Scheduler));
        if (FAILED(hr))
            throw std::runtime_error("Failed to create Scheduler instance");

        VARIANT_BOOL result;
        hr = scheduler->Connect(CComBSTR(controllerName), CComBSTR(password), CComBSTR(L"HpcTask"), NULL, &result);
        if (FAILED(hr) || result == VARIANT_FALSE)
            throw std::runtime_error("Failed to connect to HLK Controller");

        std::wcout << L"Connected to HLK Controller" << std::endl;
    }

    void RunSubTest(const wchar_t* productId, const wchar_t* subTestName)
    {
        long selectionId;
        HRESULT hr = scheduler->CreateTestSelection(CComBSTR(productId), NULL, &selectionId);
        if (FAILED(hr) || selectionId == 0)
            throw std::runtime_error("Failed to create test selection");

        CComBSTR query;
        query.Format(L"SELECT * FROM item WHERE parent=%d AND criteriaName='%s'", selectionId, subTestName);

