            const cosX = Math.cos(rotationX);
            const sinX = Math.sin(rotationX);
            const cosY = Math.cos(rotationY);
            const sinY = Math.sin(rotationY);

            const newX = point.x * cosY - point.z * sinY;
            const newY = point.y * cosX - (point.x * sinY + point.z * cosY) * sinX;
            const newZ = point.y * sinX + (point.x * sinY + point.z * cosY) * cosX;

            return { x: newX * 50 + canvas.width / 2, y: newY * 50 + canvas.height / 2 };
        }

        // Drawing the points and lines
        function drawScene(rotationX, rotationY) {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Draw points
