                ctx.fillStyle = pointColor;
                ctx.beginPath();
                const { x, y } = project3D(point, rotationX, rotationY);
                ctx.arc(x, y, pointSize / 2, 0, Math.PI * 2);
                ctx.fill();
            });

            // Draw lines
            ctx.strokeStyle = lineColor;
            ctx.lineWidth = 2;
            lines.forEach(line => {
