const canvas = document.getElementById("canvas");
const ctx = canvas.getContext("2d");

const gridSpacing = 50;
const rotSpeed = 0.01;
const snapThreshold = 5;

let lines = [];
let grid = [];
let hoveredPoint = null;
let drawingLine = false;

canvas.width = window.innerWidth;
canvas.height = window.innerHeight;

function initGrid() {
  for (let x = -canvas.width; x <= canvas.width; x += gridSpacing) {
    for (let y = -canvas.height; y <= canvas.height; y += gridSpacing) {
      for (let z = -canvas.width; z <= canvas.width; z += gridSpacing) {
        grid.push({ x, y, z });
      }
    }
  }
}

function drawLine(line) {
  ctx.beginPath();
  ctx.moveTo(line[0].x, line[0].y);
  ctx.lineTo(line[1].x, line[1].y);
  ctx.stroke();
}

function project(p) {
  let scale = canvas.width / (canvas.width + p.z);
  return { x: p.x * scale + canvas.width / 2, y: p.y * scale + canvas.height / 2 };
}

function screenDistance(p1, p2) {
  let dx = p1.x - p2.x;
  let dy = p1.y - p2.y;
  return Math.sqrt(dx * dx + dy * dy);
}

function findNearestPoint(screenCoords) {
  let minDistance = snapThreshold;
  let nearestPoint = null;

  grid.forEach(p => {
    let screenPoint = project(p);
    let distance = screenDistance(screenPoint, screenCoords);

    if (distance < minDistance) {
      minDistance = distance;
      nearestPoint = p;
    }
  });

  return nearestPoint;
}

function draw() {
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  // Draw grid points
  ctx.fillStyle = "gray";
  grid.forEach(p => {
    let screenPoint = project(p);
    ctx.fillRect(screenPoint.x - 2, screenPoint.y - 2, 4, 4);
  });

  // Draw the existing lines
  ctx.strokeStyle = "black";
  lines.forEach(line => {
    drawLine(line.map(p => project(p)));
  });

  // Draw the hovered point and potential new line
