    let screenPoint = project(hoveredPoint);

    ctx.fillStyle = "red";
    ctx.fillRect(screenPoint.x - 4, screenPoint.y - 4, 8, 8);

    if (drawingLine) {
      ctx.strokeStyle = "blue";
      drawLine([lines[lines.length - 1][0], hoveredPoint]);
    }
  }
}

canvas.addEventListener("mousemove", e => {
