class ListV2:
    def __init__(self, values=None):
        self.values = values if values is not None else []

    def __add__(self, other):
        if isinstance(other, ListV2):
            return ListV2([x + y for x, y in zip(self.values, other.values)])

    def __sub__(self, other):
        if isinstance(other, ListV2):
            return ListV2([x - y for x, y in zip(self.values, other.values)])

    def __mul__(self, other):
        if isinstance(other, ListV2):
            return ListV2([x * y for x, y in zip(self.values, other.values)])

    def __truediv__(self, other):
        if isinstance(other, ListV2):
            return ListV2([x / y for x, y in zip(self.values, other.values)])

    def append(self, value):
        self.values.append(value)

    def mean(self):
        return sum(self.values) / len(self.values)

    def __iter__(self):
        self.current = 0
        return self

    def __next__(self):
        if self.current >= len(self.values):
            raise StopIteration
        value = self.values[self.current]
        self.current += 1
        return value

    def __repr__(self):
        return str(self.values)

class DataFrame:
    def __init__(self, columns=None, data=None):
        self.columns = columns if columns is not None else []
        self.data = data if data is not None else {col: ListV2() for col in self.columns}
        self.index = dict()

    def set_index(self, column):
        index_mapper = {}
        for i, name in enumerate(self.data[column].values):
            index_mapper[name] = i
        self.index = index_mapper

    def __setitem__(self, key, value):
        if not key in self.columns:
            self.columns.append(key)
        self.data[key] = ListV2(value)

    def __getitem__(self, key):
        return self.data[key]

    def loc(self, index):
        row_index = self.index[index]
        row_data = {col: self.data[col].values[row_index] for col in self.columns}
        return row_data

    def iteritems(self):
        for col, values in self.data.items():
            yield col, values

    def iterrows(self):
        for i in range(len(self.data[self.columns[0]])):
            row_data = {col: self.data[col].values[i] for col in self.columns}
            yield i, row_data

    def astype(self, column, new_type):
        self.data[column].values = [new_type(x) for x in self.data[column].values]

    def drop(self, column):
        self.columns.remove(column)
        del self.data[column]

    def mean(self):
        mean_values = {}
        for col, values in self.data.items():
            mean_values[col] = values.mean()
        return mean_values

    def __repr__(self):
        repr = ""
        repr += ", ".join(self.columns) + "\n"
        for row_index, row_data in enumerate(zip(*[self.data[col].values for col in self.columns])):
            repr += str(row_index) + ": " + ", ".join(str(x) for x in row_data) + "\n"
        return repr

