        HandleInput();
    }

    public void TransitionToState(PlayerState newState) 
    {
        if (_currentState == newState) return; // Prevent redundant transitions

        _currentState.ExitState(this);
        _currentState = newState;
        _currentState.EnterState(this);
    }

    private void HandleInput()
    {
        if (_playerInput._moveInput != Vector2.zero && _lastMoveInput == Vector2.zero)
        {
            TransitionToState(_moveState);
        }
        else if (_playerInput._moveInput == Vector2.zero && _lastMoveInput != Vector2.zero)
        {
            TransitionToState(_idleState);
        }

        _lastMoveInput = _playerInput._moveInput; // Update last input
    }
}
