public sealed record SelectionKey(string? Residing, string? Visiting = null, string? Centre = null, string? Category = null, string? SubCategory = null)
{
    public static IEqualityComparer<SelectionKey> ResidingVisitingComparer { get; } = new ResidingVisitingEqualityComparer();
    public static IEqualityComparer<SelectionKey> CentreComparer { get; } = new CentreEqualityComparer();
    public static IEqualityComparer<SelectionKey> CategoryComparer { get; } = new CategoryEqualityComparer();

    public bool Equals(SelectionKey? other)
    {
        if (other is null) return false;
        if (ReferenceEquals(this, other)) return true;

        return string.Equals(Residing, other.Residing, StringComparison.OrdinalIgnoreCase) &&
               string.Equals(Visiting, other.Visiting, StringComparison.OrdinalIgnoreCase) &&
               string.Equals(Centre, other.Centre, StringComparison.OrdinalIgnoreCase) &&
               string.Equals(Category, other.Category, StringComparison.OrdinalIgnoreCase) &&
               string.Equals(SubCategory, other.SubCategory, StringComparison.OrdinalIgnoreCase);
    }

