    % Fit a polynomial of order k to the data
    coefs = polyfit(x_data, y_data, k);

    % Generate x values for plotting the fitted curve
    x_fit = linspace(30, 100, 100); % Adjusted to the range of data
    y_fit = polyval(coefs, x_fit); % Calculate the corresponding y values

    % Create the figure
    fig = figure;
    hold on;

    % Plot the given data points (Mathematics vs Physics)
    plot(x_data, y_data, 'r*', 'MarkerSize', 8, 'DisplayName', 'Given Marks');

    % Plot the best fit curve
    plot(x_fit, y_fit, 'b-', 'LineWidth', 2, 'DisplayName', 'Best Fit Curve');

    % Set axis limits
    axis([30 100 30 100]); % Adjusted to the range of data
    xlabel('Mathematics Marks');
    ylabel('Physics Marks');
    title('Polynomial Regression Fit');
