using UnityEngine;
using Unity.Services.CloudSave;
using Unity.Services.Core;
using System.Collections.Generic;
using System.Threading.Tasks;

public class GameManager : MonoBehaviour
{
    public static GameManager Instance;

    public int Money { get; private set; }
    public Dictionary<string, bool> UnlockedBarns { get; private set; }

    private void Awake()
    {
        if (Instance == null)
        {
            Instance = this;
            DontDestroyOnLoad(gameObject);
        }
        else
        {
            Destroy(gameObject);
        }
    }

    private async void Start()
    {
        await UnityServices.InitializeAsync();
        await LoadGameData();
    }

    public async Task LoadGameData()
    {
        try
        {
            var data = await CloudSaveService.Instance.Data.LoadAllAsync();

            Money = data.ContainsKey("Money") ? int.Parse(data["Money"]) : 0;

            UnlockedBarns = new Dictionary<string, bool>();
            if (data.ContainsKey("UnlockedBarns"))
            {
                UnlockedBarns = JsonUtility.FromJson<Dictionary<string, bool>>(data["UnlockedBarns"]);
            }
        }
        catch (CloudSaveValidationException e)
        {
            Debug.LogError($"Cloud Save Validation Exception: {e.Message}");
        }
        catch (CloudSaveException e)
        {
            Debug.LogError($"Cloud Save Exception: {e.Message}");
        }
    }

    public async Task SaveGameData()
    {
        try
        {
            var data = new Dictionary<string, object>
            {
                { "Money", Money.ToString() },
                { "UnlockedBarns", JsonUtility.ToJson(UnlockedBarns) }
            };

            await CloudSaveService.Instance.Data.ForceSaveAsync(data);
        }
        catch (CloudSaveValidationException e)
        {
            Debug.LogError($"Cloud Save Validation Exception: {e.Message}");
        }
        catch (CloudSaveException e)
        {
            Debug.LogError($"Cloud Save Exception: {e.Message}");
        }
    }

    public void AddMoney(int amount)
    {
        Money += amount;
        SaveGameData();
    }

    public bool SpendMoney(int amount)
    {
        if (Money >= amount)
        {
            Money -= amount;
            SaveGameData();
            return true;
        }
        return false;
    }

    public void UnlockBarn(string barnId)
    {
        if (!UnlockedBarns.ContainsKey(barnId))
        {
            UnlockedBarns[barnId] = true;
            SaveGameData();
        }
    }

    public bool IsBarnUnlocked(string barnId)
    {
        return UnlockedBarns.ContainsKey(barnId) && UnlockedBarns[barnId];
    }
}
```

