```scss
private async Task FilterData(_feladatokStore myFeladatokStore, List<string> tantargy, string evszam, bool emelt)
{
    await myFeladatokStore.Clear(); // Clear the feladatok list

    try
    {
        List<Feladat> feladatok = myFeladatokStore.GetAll();
        List<string> felszurtFeladat = new List<string>();

        if (tantargy != null)
        {
            feladatok = feladatok.Where(f => tantargy.Contains(f.Tantargy)).ToList();
        }

        if (evszam != null && evszam.ToLower() != "most")
        {
            feladatok = feladatok.Where(f => f.Evszam.ToLower() == evszam).ToList();
        }

        if (emelt && evszam != null && evszam.ToLower() != "most")
        {
            feladatok = feladatok.Where(f => f.Emelt).ToList();
        }

        feladatok = feladatok.Select(f => new Feladat(f)).ToList();
        feladatok = feladatok.OrderByDescending(f => f.Id).ToList();

        foreach (Feladat feladat in feladatok)
        {
            feladat.Done = feladat.Id == 2 || feladat.Id == 5;
            feladat.Szurt = feladat.Done;
            felszurtFeladat.Add(feladat.Id.ToString());
        }

        feladatok = feladatok.OrderBy(f => f.Id).ToList();

        var filteredFeladatList = feladatok.ToObservable();

        filteredFeladatList.Subscribe(f => { f.Done = false; });

        filteredFeladatList.Subscribe(f => { f.Szurt = f.Done; });

        _feladatokStore.Feladatok = felszurtFeladat;
    }
    catch (Exception ex)
    {
        Debug.WriteLine(ex.Message);
    }
}
```
