function frame() {
  if (frameCount < 60) {
    canvas.background(255);
    const pixels = video.getPixels();
    const asciiChars = new Array(pixels.length);
    for (let i = 0; i < pixels.length; i++) {
      const alpha = pixels[i * 4 + 3];
      const red = pixels[i * 4 + 0];
      const green = pixels[i * 4 + 1];
      const blue = pixels[i * 4 + 2];
      const brightness = (red + green + blue) / 3;
      const threshold = 100; // user can change this value
      asciiChars[i] = threshold > brightness ? '1' : '0';
    }
    const encoded = encodeText(asciiChars.join(''), 32, 96, 127);
    canvas.fill(255);
    canvas.textSize(16);
    canvas.color("#000000");
    canvas.text(encoded, width / 2, height / 2, WIDTH, HEIGHT);
    video.resetFrameState();
  }
}

function updateVideo() {
  videoFile = element("input[name='videoFile']").value;
  videoFile = videoFile.replace(/^C:\\/, '');
  element("canvas").src = "";
  element("canvas").src = videoFile;
}
