```javascript
function generateImageSet(totalImages = 450) {
  const classes = 4;
  const distortions = 3;
  const levels = 3;
  const imagesPerClass = 256;
  const totalCombinations = classes * distortions * levels;

  // Create an array of all possible combinations
  let allCombinations = [];
  for (let c = 0; c < classes; c++) {
    for (let d = 0; d < distortions; d++) {
      for (let l = 0; l < levels; l++) {
        allCombinations.push({ class: c, distortion: d, level: l });
      }
    }
  }

  // Shuffle the combinations
  allCombinations = shuffleArray(allCombinations);

  // Calculate how many times we need to repeat the combinations
  const repetitions = Math.ceil(totalImages / totalCombinations);

  let selectedImages = [];
  let usedReferences = new Set();

  for (let i = 0; i < repetitions; i++) {
    for (let combo of allCombinations) {
      if (selectedImages.length >= totalImages) break;

      let referenceImage;
      do {
        referenceImage = Math.floor(Math.random() * imagesPerClass) + 1 + (combo.class * imagesPerClass);
      } while (usedReferences.has(referenceImage));

      usedReferences.add(referenceImage);

      selectedImages.push({
        referenceImage: referenceImage,
        class: combo.class,
        distortion: combo.distortion,
        level: combo.level
      });
    }
  }

  // Shuffle the final selection to avoid any patterns
  return shuffleArray(selectedImages);
}

// Fisher-Yates shuffle algorithm
function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}

