          apiVersion: '2020-08-27',
        });
      },
    },
  ],
  exports: ['STRIPE'],
})
export class StripeModule {}
```

Replace `'your_stripe_secret_key'` with your Stripe secret key.

Now, you can use the Stripe instance in your application services.

3. Create a PriceService.

Create a new file `price.service.ts`.

```typescript
import { Injectable } from '@nestjs/common';
import { Inject } from '@nestjs/common/decorators/core/component.decorators';
import Stripe from 'stripe';

@Injectable()
export class PriceService {
  constructor(@Inject('STRIPE') private readonly stripe: Stripe) {}

  async createProductWithPrices(
    name: string,
    prices: { amount: number; currency: string; nickname: string }[],
  ): Promise<Stripe.Product & { prices: Stripe.Price[] }> {
    const product = await this.stripe.products.create({ name });

    const pricePromises = prices.map((price) =>
      this.stripe.prices.create({
        unit_amount: price.amount,
        currency: price.currency,
        product: product.id,
        nickname: price.nickname,
      }),
    );

    const stripePrices = await Promise.all(pricePromises);

    return {
      ...product,
      prices: stripePrices,
    };
  }
}
```

