public:
    void ConnectToHLK(const wchar_t* controllerName, const wchar_t* password)
    {
        CoInitialize(NULL);
        HRESULT hr = scheduler.CoCreateInstance(__uuidof(Scheduler));
        if (FAILED(hr))
            throw std::runtime_error("Failed to create Scheduler instance");

        VARIANT_BOOL result;
        hr = scheduler->Connect(CComBSTR(controllerName), CComBSTR(password), CComBSTR(L"HpcTask"), NULL, &result);
        if (FAILED(hr) || result == VARIANT_FALSE)
            throw std::runtime_error("Failed to connect to HLK Controller");

