            subTest->GetValue(0, CComBSTR(L"id"), &subTestId);

            hr = scheduler->ChangeSelectionItemsState(CComBSTR(L"item"), subTestId.lVal, CComBSTR(L"Selected"));
            if (FAILED(hr))
                throw std::runtime_error("Failed to select sub-test");

            std::wcout << L"Selected sub-test: " << subTestName << std::endl;

            long testResultId;
            hr = scheduler->RunSelection(selectionId, &testResultId);
            if (FAILED(hr) || testResultId == 0)
                throw std::runtime_error("Failed to run test selection");

            std::wcout << L"Running test selection with ID: " << testResultId << std::endl;
        }
        else
        {
            throw std::runtime_error("Failed to find sub-test");
        }
    }

    ~HLKAutomation()
    {
        CoUninitialize();
    }
};
```

