        return send_file(os.path.join(directory, filename), as_attachment=True)
    else:
        return "File not found or expired. Please upload again."

@app.before_first_request
def remove_old_files():
    now = datetime.now()
    for filename in os.listdir(app.config['UPLOAD_FOLDER']):
        filetime = datetime.fromtimestamp(os.path.getctime(os.path.join(app.config['UPLOAD_FOLDER'], filename)))
        if now - filetime > timedelta(minutes=2):
            os.remove(os.path.join(app.config['UPLOAD_FOLDER'], filename))

if __name__ == "__main__":
