// Output: Schedule of largest number of compatible intervals

function earliest_start(intervals: array):
  sort intervals by increasing start time
  compatible_intervals := set()

  for i in intervals:
    compatible := true
    for j in compatible_intervals:
      if i.overlaps(j):
        compatible := false

    if compatible:
      compatible_intervals.add(i)

  return compatible_intervals
