Here is a basic implementation of a JavaScript 3D matrix wireframe grid array using only the standard HTML5 Canvas API (no libraries or frameworks). This example allows you to draw lines by snapping them to the grid, then you can rotate the model in any direction and continue drawing.

```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>3D Matrix Wireframe Grid Array</title>
    <style>
        canvas {
            display: block;
            margin: auto;
        }
    </style>
</head>
<body>
    <canvas id="canvas" width="800" height="600"></canvas>
    <script>
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext('2d');

        const matrixSize = 20;
        const pointSize = 4;
        const pointColor = '#00FF00';
        const lineColor = '#FFFFFF';

        let points = [];
        let lines = [];
        let selectedPoint = null;
        let isDrawing = false;

        for (let x = -matrixSize; x <= matrixSize; x++) {
            for (let y = -matrixSize; y <= matrixSize; y++) {
                for (let z = -matrixSize; z <= matrixSize; z++) {
                    points.push({ x, y, z });
                }   
            }
        }

        // Projection onto 2D canvas
        function project3D(point, rotationX, rotationY, rotationZ) {
            const sinX = Math.sin(rotationX);
            const cosX = Math.cos(rotationX);
            const sinY = Math.sin(rotationY);
            const cosY = Math.cos(rotationY);
            const sinZ = Math.sin(rotationZ);
            const cosZ = Math.cos(rotationZ);

            let newX = point.x;
            let newY = point.y * cosX - point.z * sinX;
            let newZ = point.y * sinX + point.z * cosX;

            const finalX = newX * cosY - newZ * sinY;
            const finalY = newY * cosZ - newX * sinY * sinZ;

            return { x: finalX * 20 + canvas.width / 2, y: finalY * 20 + canvas.height / 2 };
        }

        // Drawing the points and lines
        function drawScene(rotationX, rotationY, rotationZ) {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            points.forEach(point => {
                ctx.fillStyle = pointColor;
                ctx.beginPath();
                const { x, y } = project3D(point, rotationX, rotationY, rotationZ);
                ctx.arc(x, y, pointSize / 2, 0, Math.PI * 2);
                ctx.fill();
            });

            ctx.strokeStyle = lineColor;
            ctx.lineWidth = 2;
            lines.forEach(line => {
                const start = project3D(points[line[0]], rotationX, rotationY, rotationZ);
                const end = project3D(points[line[1]], rotationX, rotationY, rotationZ);

                ctx.beginPath();
                ctx.moveTo(start.x, start.y);
                ctx.lineTo(end.x, end.y);
                ctx.stroke();
            });
        }

        function draw() {
            drawScene(rotationX, rotationY, rotationZ);
            requestAnimationFrame(draw);
        }

        function getCursorPos(canvas, e) {
            const rect = canvas.getBoundingClientRect();
            return { x: e.clientX - rect.left, y: e.clientY - rect.top };
        }

        function selectPoint(x, y) {
            const size = pointSize / 2;

            for (let i = 0; i < points.length; i++) {
                const { x: px, y: py } = project3D(points[i], rotationX, rotationY, rotationZ);

                if (x >= px - size && x <= px + size && y >= py - size && y <= py + size) {
                    return i;
                }
            }

            return null;
        }

        // Event listeners
        let rotationX = 0;
        let rotationY = 0;
        let rotationZ = 0;
        let prevX = 0;
        let prevY = 0;

        canvas.addEventListener('mousedown', e => {
            const { x, y } = getCursorPos(canvas, e);
            prevX = x;
            prevY = y;

            if (!isDrawing) {
                selectedPoint = selectPoint(x, y);
            }
        });

        canvas.addEventListener('mousemove', e => {
            const { x, y } = getCursorPos(canvas, e);

            if (e.buttons === 1 && !isDrawing) {
                rotationY += (x - prevX) * 0.01;
                rotationX += (y - prevY) * 0.01;
                prevX = x;
                prevY = y;
            }
        });

        canvas.addEventListener('mouseup', e => {
            if (isDrawing && selectedPoint !== null) {
                const { x, y } = getCursorPos(canvas, e);

                const endPoint = selectPoint(x, y);
                if (endPoint !== null) {
                    lines.push([selectedPoint, endPoint]);
                }
            }

            selectedPoint = null;
        });

        document.addEventListener('keydown', e => {
            if (e.key === 'Enter') {
                isDrawing = !isDrawing;
            }
        });

        draw();
    </script>
</body>
