        const pointSize = 4;
        const pointColor = '#00FF00';
        const lineColor = '#FFFFFF';

        let points = [];
        let lines = [];
        let selectedPoint = null;
        let isDrawing = false;

        for (let x = -matrixSize; x <= matrixSize; x++) {
            for (let y = -matrixSize; y <= matrixSize; y++) {
                for (let z = -matrixSize; z <= matrixSize; z++) {
                    points.push({ x, y, z });
                }   
            }
        }

        // Projection onto 2D canvas
        function project3D(point, rotationX, rotationY, rotationZ) {
            const sinX = Math.sin(rotationX);
            const cosX = Math.cos(rotationX);
            const sinY = Math.sin(rotationY);
            const cosY = Math.cos(rotationY);
            const sinZ = Math.sin(rotationZ);
            const cosZ = Math.cos(rotationZ);

            let newX = point.x;
            let newY = point.y * cosX - point.z * sinX;
            let newZ = point.y * sinX + point.z * cosX;

            const finalX = newX * cosY - newZ * sinY;
            const finalY = newY * cosZ - newX * sinY * sinZ;

            return { x: finalX * 20 + canvas.width / 2, y: finalY * 20 + canvas.height / 2 };
        }

        // Drawing the points and lines
        function drawScene(rotationX, rotationY, rotationZ) {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            points.forEach(point => {
                ctx.fillStyle = pointColor;
                ctx.beginPath();
                const { x, y } = project3D(point, rotationX, rotationY, rotationZ);
                ctx.arc(x, y, pointSize / 2, 0, Math.PI * 2);
                ctx.fill();
            });

            ctx.strokeStyle = lineColor;
            ctx.lineWidth = 2;
            lines.forEach(line => {
                const start = project3D(points[line[0]], rotationX, rotationY, rotationZ);
                const end = project3D(points[line[1]], rotationX, rotationY, rotationZ);

                ctx.beginPath();
                ctx.moveTo(start.x, start.y);
                ctx.lineTo(end.x, end.y);
                ctx.stroke();
            });
        }

